import React, { useState, useEffect, useRef, useCallback } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronCircleLeft, faChevronCircleRight } from '@fortawesome/free-solid-svg-icons';

const Container = styled.div`
    width:100%;
    height:auto;
    overflow:hidden;
    position:relative;
`;

const Slide = styled.div`
    width:100%;
    height:auto;
    overflow: visible;
    display:flex;

    transition: all 0.5s ease-in-out;
`;

const SlideBtn = styled.button`
    font-size:1.6rem;
    color:rgba(0, 0, 0, 0.07);
`;  

const LeftSlideBtn = styled(SlideBtn)`
    position:absolute;
    top:50%;
    left:5px;
    z-index:1;
`;

const RightSlideBtn = styled(SlideBtn)`
    position:absolute;
    top:50%;
    right:5px;
`;

const PostContents = ({ contents }) => {
    const [curSlide, setCurSlide] = useState(0);
    const [leftBtn, setLeftBtn] = useState(false);
    const [rightBtn, setRigthBtn] = useState(false);
    const slideRef = useRef(null);

    useEffect(() => {
        if(curSlide>0) {
            setLeftBtn(true);
        } else {
            setLeftBtn(false);
        }

        if(curSlide<contents.length-1) {
            setRigthBtn(true);
        } else {
            setRigthBtn(false);
        }

        slideRef.current.style.transform = `translateX(-${curSlide}00%)`;

    }, [curSlide]);

    const onClickRightBtn = useCallback(() => {
        setCurSlide(curSlide+1);
    }, [curSlide]);

    const onClickLeftBtn = useCallback(() => {
        setCurSlide(curSlide-1);
    }, [curSlide]);

    return (
        <Container>
            {leftBtn && <LeftSlideBtn className="btn-hover" onClick={onClickLeftBtn}>
                            <FontAwesomeIcon icon={faChevronCircleLeft} />
                        </LeftSlideBtn>}
            <Slide ref={slideRef}>
                {contents.map((url, i) => <img key={url+i} src={url} style={{width:'100%', height:'auto', flexShrink:'0'}} />)}
            </Slide>
            {rightBtn && <RightSlideBtn className="btn-hover" onClick={onClickRightBtn}>
                            <FontAwesomeIcon icon={faChevronCircleRight} />
                        </RightSlideBtn>}
        </Container>
    );
};

PostContents.propTypes = {
    contents: PropTypes.arrayOf(PropTypes.string),
};

export default PostContents;
