import React from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart as heart, faComment, faBookmark } from '@fortawesome/free-regular-svg-icons';
import { faHeart as filledHeart } from '@fortawesome/free-solid-svg-icons';

const BarIcons = styled.div`
    border:1px solid black;
    height:50px;
    padding:0 3%;
    font-size:1.7rem;

    display:flex;
    justify-content:space-between;
    align-items:center;
`;

const BtnContainer = styled.div`
    width:20%;
    height:100%;

    display:flex;
    align-items:center;
`;

const PageDotContainer = styled.div`
    width:60%;
    height:100%;

    display:flex;
    align-items:center;
`;

const MidBar = ({ postId, liked }) => {
    return(
        <BarIcons>
            <BtnContainer>
                {liked ? <FontAwesomeIcon icon={filledHeart} /> : <FontAwesomeIcon icon={heart} />}
                <FontAwesomeIcon icon={faComment} style={{marginLeft:'20px'}} />
            </BtnContainer>
            <PageDotContainer>
            this is a bar;
            </PageDotContainer>
            <BtnContainer style={{justifyContent:'flex-end'}}>
                <FontAwesomeIcon icon={faBookmark} />
            </BtnContainer>
        </BarIcons>
    );
};

MidBar.propTypes = {
    postId: PropTypes.string,
    liked: PropTypes.bool,
};

export default MidBar;
