import React from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart as heart, faComment, faBookmark } from '@fortawesome/free-regular-svg-icons';
import { faHeart as filledHeart, faCircle } from '@fortawesome/free-solid-svg-icons';

import theme from '../style/theme';

const BarIcons = styled.div`
    height:50px;
    padding:0 3%;
    font-size:1.6rem;

    display:flex;
    justify-content:space-between;
    align-items:center;
`;

const BtnContainer = styled.div`
    width:20%;
    height:100%;

    display:flex;
    align-items:center;
`;

const PageDotContainer = styled.div`
    width:60%;
    height:100%;
    font-size:7px;

    display:flex;
    justify-content:center;
    align-items:center;
`;

// icon={faCircle} 속성 추가
const GreyDot = styled(FontAwesomeIcon)`
    margin:0 2px;
    color:${theme.borderColorDark};
`;

const BlueDot = styled(FontAwesomeIcon)`
    margin:0 2px;
    color:${theme.btnColor};
`;

const MidBar = ({ postId, liked, curSlide, contentLen }) => {
    console.log(contentLen);
    return(
        <BarIcons>
            <BtnContainer>
                {liked ? <FontAwesomeIcon icon={filledHeart} /> : <FontAwesomeIcon icon={heart} />}
                <FontAwesomeIcon icon={faComment} style={{marginLeft:'20px'}} />
            </BtnContainer>
            <PageDotContainer>
                {Array(contentLen).fill().map((v,i) => {
                    console.log(v, i);
                    if(curSlide === i) {
                        <BlueDot icon={faCircle} />
                    } else {
                        <GreyDot icon={faCircle} />
                    }
                })}
            </PageDotContainer>
            <BtnContainer style={{justifyContent:'flex-end'}}>
                <FontAwesomeIcon icon={faBookmark} />
            </BtnContainer>
        </BarIcons>
    );
};

MidBar.propTypes = {
    postId: PropTypes.string,
    liked: PropTypes.bool,
    curSlide: PropTypes.number,
    contentLen: PropTypes.number,
};

export default MidBar;
