const initialState = {
    mainPosts:[{
        id:'1',
        User: {
            id: 'ote8125',
            nickname: 'ooo__tanney',
            profilePic: '/img/profile_tanney.png',
        },
        contents:[
            '/img/react-img.png',
            'http://placehold.it/300x300',
            'http://placehold.it/300x300',
            'http://placehold.it/300x300',
            'http://placehold.it/300x300',
        ],
        likes:5,
        liked:false,
        marked:false,
        description: 'dummy data',
        Comments: [{
            User:{
                nickname: 'Danney',
                profilePic:'',
            },
            content: 'This is a comment',
            reply:[],
            likes:3,
        }],
    }]
};

const PUSH_LIKE = 'PUSH_LIKE';

export const pushLikeDyn = id => {
    return {
        type: PUSH_LIKE,
        id,
    }
}


const reducer = (state = initialState, action) => {
    switch(action.type) {
        case PUSH_LIKE:
            let idx = 0;
            const curPost = state.mainPosts.map((p, i) => {
                if(p.id === action.id) {
                    idx = i;
                    return p;
                }
            })[0];

            const newPost = {...curPost, likes:curPost.likes+1, liked:true};
            const mainPosts = [...state.mainPosts];
            mainPosts[idx] = newPost;

            return {
                ...state,
                mainPosts,
            };
        default:
            return state;
    }
};

export default reducer;
