import React, { useCallback } from 'react';
import styled from 'styled-components';
import PropTypes from 'prop-types';
import { useDispatch } from 'react-redux';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart as heart, faComment, faBookmark } from '@fortawesome/free-regular-svg-icons';
import { faHeart as filledHeart, faCircle } from '@fortawesome/free-solid-svg-icons';

import theme from '../style/theme';
import { pushLike } from '../reducers/post';

const BarIcons = styled.div`
    height:50px;
    padding:0 3%;
    font-size:1.6rem;

    display:flex;
    justify-content:space-between;
    align-items:center;
`;

const BtnContainer = styled.div`
    width:20%;
    height:100%;

    display:flex;
    align-items:center;
`;

const PageDotContainer = styled.div`
    width:60%;
    height:100%;
    font-size:7px;

    display:flex;
    justify-content:center;
    align-items:center;
`;

// icon={faCircle} 속성 추가
const GreyDot = styled(FontAwesomeIcon)`
    margin:0 2px;
    color:${theme.borderColorDark};
`;

const BlueDot = styled(FontAwesomeIcon)`
    margin:0 2px;
    color:${theme.btnColor};
`;

const MidBar = ({ post, curSlide, contentLen }) => {
    const dispatch = useDispatch();

    const onClickLikeBtn = useCallback(() => {
        console.log('clicked');
        // dispatch(pushLike);
    }, []);

    return(
        <BarIcons>
            <BtnContainer>
                {post.liked 
                ? <button onClick={onClickLikeBtn}><FontAwesomeIcon icon={filledHeart} className="btn-hover" /></button>
                : <FontAwesomeIcon icon={heart} className="btn-hover" />}
                <FontAwesomeIcon icon={faComment} className="btn-hover" style={{marginLeft:'20px'}} />
            </BtnContainer>
            <PageDotContainer>
                {Array(contentLen).fill().map((v,i) => {
                    if(curSlide === i) {
                        return <BlueDot key={i} icon={faCircle} />
                    } else {
                        return <GreyDot key={i} icon={faCircle} />
                    }
                })}
            </PageDotContainer>
            <BtnContainer style={{justifyContent:'flex-end'}}>
                <FontAwesomeIcon icon={faBookmark} className="btn-hover" />
            </BtnContainer>
        </BarIcons>
    );
};

MidBar.propTypes = {
    post: PropTypes.shape({
        id: PropTypes.string,
        User: PropTypes.shape({
            nickname: PropTypes.string,
            profilePic: PropTypes.string,
        }),
        contents:PropTypes.arrayOf(PropTypes.string),
        likes: PropTypes.number,
        description: PropTypes.string,
        Comments: PropTypes.arrayOf(PropTypes.object),
    }).isRequired,
    curSlide: PropTypes.number,
    contentLen: PropTypes.number,
};

export default MidBar;
